import useSWR from 'swr';
import { useState } from 'react';
const endpoint = "http://localhost:4005/videos";

function Mutate() {
  const { data, mutate } = useSWR(endpoint);
const[newUser,setNewUser]=useState({id:"",title:"",author:""})
  const newData = [
    {"id":1, "title":"useSWR ile veri yakalama ve alma", "author":"Kamil"},
    {"id":2, "title":"React kullanımı", "author":"Ahmet"},
    {"id":3, "title":"Fetcher", "author":"Mehmet"},
    {"id":4, "title":"Cache de veriyi depolar", "author":"Cengiz"},
    {"id":5, "title":"Birden fazla component kullanımında cachdeki veriyi verir.", "author":"Ali"},
    {"id":6, "title":"Yeni veri 1", "author":"Emre"},
    {"id":7, "title":"Yeni veri 2", "author":"Can"},
    {"id":8, "title":"Yeni veri 3", "author":"Ayşe"}
  ];
console.log(...newData)
let newData2=[{"id":2, "title":"React kullanımı", "author":"Ahmet"},]
  const updateData = async (e) => {
    e.target.PreventDefault();
    mutate([...newData,newUser],false);
   
  }

  console.log("Eski veri: ", data);
  console.log("Yeni veri: ", newData);

console.log(newUser)
  return (
    <div>
    <form action="#">
    <input type="text"  placeholder='id' onChange={(e)=>(setNewUser({...newUser,id:e.target.value}))}/>
    <input type="text"  placeholder='title' onChange={(e)=>(setNewUser({...newUser,title:e.target.value}))}/>
    <input type="text"  placeholder='author' onChange={(e)=>(setNewUser({...newUser,author:e.target.value}))}/>
    <button onClick={updateData}>Veri Ekle</button>
    </form>
      { data && data.map(item => <p key={item.id}>{item.title}</p>) }
    </div>
  );
}

export default Mutate;
