import useSWR from 'swr';

const endpoint = "http://localhost:4005/videos";

function Mutate() {
  const { data, mutate } = useSWR(endpoint);

  const newData = [
    {"id":1, "title":"useSWR ile veri yakalama ve alma", "author":"Kamil"},
    {"id":2, "title":"React kullanımı", "author":"Ahmet"},
    {"id":3, "title":"Fetcher", "author":"Mehmet"},
    {"id":4, "title":"Cache de veriyi depolar", "author":"Cengiz"},
    {"id":5, "title":"Birden fazla component kullanımında cachdeki veriyi verir.", "author":"Ali"},
    {"id":6, "title":"Yeni veri 1", "author":"Emre"},
    {"id":7, "title":"Yeni veri 2", "author":"Can"},
    {"id":8, "title":"Yeni veri 3", "author":"Ayşe"}
  ];


  const updateData = async () => {
    mutate(endpoint,[  {"id":1, "title":"useSWR ile veri yakalama ve alma", "author":"Kamil"},],false);
  }

  console.log("Eski veri: ", data);
  console.log("Yeni veri: ", newData);

  console.log(data)
  return (
    <div>
      <button onClick={updateData}>Veriyi Güncelle</button>
      { data && data.map(item => <p key={item.id}>{item.title}</p>) }
    </div>
  );
}

export default Mutate;
